package global;

public class Lugar {
	
	private String nomeLugar;
	private String pais;

	public Lugar(String nomeLugar, String pais) {
		// TODO Auto-generated constructor stub
		this.nomeLugar = nomeLugar;
		this.pais = pais;
	}
	
	//setters:
	public void setNomeLugar (String nol) {
		nomeLugar = nol;
	}
	
	public void setPais (String pal) {
		pais = pal;
	}
	
	//getters:
	
	public String getNomeLugar() {
		return nomeLugar;
	}
	
	public String getPais() {
		return pais;
	}
	
	//metodos:
	
	
	
	
	
	
	
	
	

}
